import { Component } from 'react';

import CardList from './component/card-list/cradlist.component';
import SearchBox from './component/search-box/search-box.component';

import './App.css';

class App extends Component {
  constructor() {
    super();

    this.state = {
     monstoers: [],
     searchField: '',
    };
  }

  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/users')
    .then((response) => response.json()).
    then((users) => this.setState(() => {
      return {monstoers: users};
    }));
  }

  onSearchField = (event) => {
    const searchField = event.target.value.toLocaleLowerCase()
    this.setState(() => {
      return {searchField};
    });
  }

  render() {    console.log('render')
    const {monstoers, searchField} = this.state;
    const {onSearchField} = this;

    const filterdMonsters = monstoers.filter((monster) => {
      return monster.name.toLocaleLowerCase().includes(searchField);
    });
    return (
      <div className="App">
      <SearchBox 
      onSearchHandler={onSearchField}
      className='monsters-search-box'
      placeholder='search monsters'
      />
      <h1 className='app-title'>Monsters Rolodex</h1>
      <CardList monstoers={filterdMonsters}/>
      </div>
    );
  }
}

export default App;
